sp_widget.client_script:
api.controller = function ($scope) {
  /* widget controller */
  var c = this;
  c.rehighlight = rehighlight;
  window.onload = init;

	addEventListener("keydown", function (e) {
		console.log(e);
		if (e && e.key == "Enter") {
			var userInput = e.target.textContent.substr(0, e.target.textContent.length);
			writeOut(userInput);
			e.target.textContent = "";
		}

	});

  function init () {
    var userInputElement = document.getElementById("user-input");
    userInputElement.contentEditable = true;
    userInputElement.focus();
    //window.Prism.highlightAll();
  	addSampleOutput();
	}

  function rehighlight () {
    //window.Prism.highlightAll();
  }

	function addSampleOutput () {
		writeOut("Testing Message");
	}


	function writeOut (message) {
		var msg = message || "";
		var output = document.createElement("span");
		var textForOutput = "  >  " + msg;
		output.textContent = textForOutput;
		output.classList.add("stuck-bottom");
		document.getElementById("output").appendChild(output);
	}


};

