sp_widget.client_script:
api.controller = function ($scope) {
  /* widget controller */
  var c = this;
  c.rehighlight = rehighlight;

  window.onload = init;

  function init () {
    var editor = document.getElementById("editor");
    editor.contentEditable = true;
    editor.focus();
    window.Prism.highlightAll();
  }

  function rehighlight () {
    window.Prism.highlightAll();
  }

};

sp_widget.css:
.token.operator {
    background: none;
}


#editor {
    display: flex;
    width: 100%;
    height: 50vh;
    resize: both;
}

sp_widget.template:
&lt;script&gt;
  window.Prism = window.Prism || {};
  window.Prism.manual = true;
&lt;/script&gt;

<code id="editor" class="language-javascript"></code>

<button type="button" ng-click="c.rehighlight()">refresh highlight</button>
