sp_widget.client_script:
api.controller = function ($scope) {
  /* widget controller */
  var c = this;
  c.loadEditor = loadEditor;
  c.activeEditor;

  var opts = {
    container: "epiceditor",
    textarea: "my-edit-area",
    basePath: '',
    clientSideStorage: true,
    localStorageName: 'epiceditor',
    useNativeFullscreen: true,
    parser: marked,
    file: {
      name: 'epiceditor',
      defaultContent: '',
      autoSave: 100
    },
    theme: {
      base: '8aa2772b47e731101958ae4b116d43f0.cssdbx',
      preview: '633337e747e731101958ae4b116d43a9.cssdbx',
      editor: '3cc2ffe747e731101958ae4b116d43df.cssdbx'
    },
    button: {
      preview: true,
      fullscreen: true,
      bar: "auto"
    },
    focusOnLoad: true,
    shortcut: {
      modifier: 18,
      fullscreen: 70,
      preview: 80
    },
    string: {
      togglePreview: 'Toggle Preview Mode',
      toggleEdit: 'Toggle Edit Mode',
      toggleFullscreen: 'Enter Fullscreen'
    },
    autogrow: false
  };

  setTimeout(function () {c.editor = new EpicEditor(opts).load(cbLog("onload ran"));}, 5000);

  function loadEditor () {
    if (c.editor) {
      c.editor.unload(cbLog("Editor unloaded."));
      c.editor = undefined;
    }

    c.editor = new EpicEditor(opts).load(cbLog("Editor loaded."));
  }

  function reflowEditor () {
    c.editor.reflow();
  }

  function cbLog (msg) {
    console.log(msg);
  }
};
